          <div className="repo-name-div">
            <p className="repo-name" style={{ color: theme.text }}>
              {repo.name}
            </p>
          </div>
          <div className="repo-meta" style={{ color: theme.secondaryText }}>
            <span className="repo-meta-line">{repo.role} Â· {repo.company}</span>
            <span className="repo-meta-line">{repo.timeframe}</span>
          </div>
